import React, { useRef, useState, useContext } from "react";
import { Box, Button, Card, Flex } from "@chakra-ui/react";
import Slider from "./Slider";
import CanvasDraw from "react-canvas-draw";
import Options from "./Options";
import { BsEraser, BsStars } from "react-icons/bs";
import { BiUndo } from "react-icons/bi";
import UserContext from "../../context/userContext";
import ChatArea from "../../components/ChatArea/ChatArea";
import { Spinner } from "@chakra-ui/react";
import useApi from "../../hooks/useApi";

const Home = () => {
  const ref = useRef(null);
  const [magicEraser, setMagicEraser] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [productsLoading, setProductsLoading] = useState(false);
  const [products, setProducts] = useState([]);
  const [image, setImage] = useState("https://i5.walmartimages.com/asr/ba4e9907-90fd-4e5b-9aa2-f4f717d94974.db9c0871bf9f02cb80d1bbedb2f3e0b7.jpeg?odnHeight=768&odnWidth=768&odnBg=FFFFFF");
  const { getProducts, getPromptWithMaskedImage } = useApi();

  const onData = (image) => {
    setImage(image);
  }

  const handleGetProducts = () => {
    setProductsLoading(true);
    getProducts(image).then((res) => {
      setProducts(res.data.products);
      setProductsLoading(false);
    });
  }

  const getMaskedImage = (flag) => {
    // return ""
    return ref.current.getDataURL("base64", true, flag ? "black" : "white").split(",")[1]
  }


  const User = useContext(UserContext).user;

  return (
    <Flex gap="10px" height="94%" padding="0 10px">
      <ChatArea onData={onData} setIsLoading={setIsLoading} getMaskedImage={getMaskedImage} image={image} />
      <Card
        padding="30px"
        height="99%"
        width="45%"
        gap={10}
        style={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        <Flex direction="column" alignItems="center">
          {!isLoading ? (
            <CanvasDraw
              imgSrc={image}
              style={{
                borderRadius: '10px'
              }}
              brushColor="#fff"
              ref={ref}
              canvasWidth={768}
              canvasHeight={768}
              disabled={!magicEraser}
              brushRadius={20}
            />


          ) : (
            <Spinner />
          )}
          <Options
            options={[
              {
                name: "Erase All",
                icon: <BsEraser size={20} />,
                onClick: () => ref.current.eraseAll(),
              },
              {
                name: "Magic Eraser",
                icon: (
                  <BsStars size={20} />
                ),
                onClick: () => {
                  ref.current.eraseAll();
                  setMagicEraser((prev) => !prev);
                },
              },
              {
                name: "Undo",
                icon: <BiUndo size={20} />,
                onClick: () => ref.current.undo(),
              },
            ]}
            magicEraser={magicEraser}
          />
        </Flex>
        <Flex display={'flex'} justifyContent={'center'} w={'full'}>
          <Button onClick={handleGetProducts}>
            Get Products
          </Button>
        </Flex>
      </Card>
      <Slider loading={productsLoading} products={products} />
    </Flex>
  );
};

export default Home;
