import React, {useEffect, useRef, useState} from 'react';
import {Button, Card, Flex, Input, Spinner, Tag, TagLabel, TagRightIcon, Text, Image} from "@chakra-ui/react";
import CanvasDraw from "react-canvas-draw";
import Options from "../Editor/Options";
import {BsEraser, BsStars} from "react-icons/bs";
import {BiFemale, BiMale, BiUndo} from "react-icons/bi";
import useApi from "../../hooks/useApi";
import {AiOutlineSend} from "react-icons/ai";
import Slider from "../Editor/Slider";

const pointer = {
    cursor: "pointer",
};

const submitContainer = {
    //   position: "absolute",
    bottom: "0px",
    width: "100%",
};

const Recommender = () => {
    const ref = useRef(null);
    const [magicEraser, setMagicEraser] = useState(false);
    const [productsLoading, setProductsLoading] = useState(false);
    const [error, setError] = useState("");
    const [info, setInfo] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [image, setImage] = useState("https://themannequin.com/cdn/shop/products/HERRE2MaleMattWhiteMannequin_1_800x.jpg?v=1638932357");
    const [gender, setGender] = useState("male");
    const [products, setProducts] = useState([]);
    const [links, setLinks] = useState([]);

    const {getRecommendation, getProducts} = useApi();

    useEffect(() => {
        if (gender === 'male') {
            setImage("https://themannequin.com/cdn/shop/products/HERRE2MaleMattWhiteMannequin_1_800x.jpg?v=1638932357");
        } else {
            setImage("https://themannequin.com/cdn/shop/products/PF1_01Front_800x.jpg?v=1563764064");
        }
    }, [gender]);

    const handleGetProducts = () => {
        setProductsLoading(true);
        getProducts(image).then((res) => {
            setProducts(res.data.products);
            setProductsLoading(false);
        });
    }

    const getMaskedImage = () => {
        return "";
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        setIsLoading(true)

        try {
            const res = await getRecommendation(links, image, getMaskedImage());

            setError("");
            setInfo("Recommendation successful!");

            setImage(res.data.image);
            setIsLoading(false);
        } catch (err) {
            setIsLoading(false);
            if (err.response && (err.response.status === 401 || err.response.status === 403)) {
                setError("Invalid email or password");
            } else {
                setError("Something went wrong");
            }
        }
    }

    return (
        <Flex gap="10px" height="100%" padding="10px">
            <Card
                border="0.0625rem solid #2d3748"
                padding="10px 10px 0px 10px"
                gap="3px"
                height="99%"
                width="25%"
                maxHeight="99%"
                justifyContent={'space-around'}
            >
                <Flex
                    overflowY="scroll"
                    gap={"20px"}
                    height="90%"
                    minH={"90%"}
                    maxH={"90%"}
                    direction="column"
                >
                    <Flex
                        mb={"5px"}
                        direction={"column"}
                        border="0.0625rem solid #3c4a61"
                        borderRadius={10}
                        p={"20px"}
                    >
                        <Text
                            mb="8px"
                            textTransform="uppercase"
                            fontWeight="500"
                            fontSize="16px"
                        >
                            Gender
                        </Text>
                        <Flex gap={"20px"}>
                            <Tag
                                size="lg"
                                borderRadius="full"
                                colorScheme={gender === "male" ? "teal" : "gray"}
                                onClick={() => {
                                    setGender("male");
                                }}
                                sx={pointer}
                            >
                                <TagLabel>Male</TagLabel>
                                <TagRightIcon as={BiMale}/>
                            </Tag>
                            <Tag
                                size="lg"
                                borderRadius="full"
                                colorScheme={gender !== "male" ? "teal" : "gray"}
                                onClick={() => {
                                    setGender("female");
                                }}
                                sx={pointer}
                            >
                                <TagLabel>Female</TagLabel>
                                <TagRightIcon as={BiFemale}/>
                            </Tag>
                        </Flex>
                    </Flex>
                    <Flex gap="20px" flexDirection="column">
                        <Flex
                            flexDirection="column"
                            border="0.0625rem solid #3c4a61"
                            borderRadius={10}
                            p={"10px"}
                        >
                            <Text
                                mb="8px"
                                textTransform="uppercase"
                                fontWeight="500"
                                fontSize="16px"
                            >
                                Product Links
                            </Text>
                            {
                                links.map((link, index) => (
                                    <Flex key={index} style={{
                                        marginBottom: '10px'
                                    }}>
                                        <Input
                                            placeholder="Enter Product Link"
                                            value={link}
                                            onChange={(e) => {
                                                const newLinks = [...links];
                                                newLinks[index] = e.target.value;
                                                setLinks(newLinks);
                                            }}
                                        />
                                    </Flex>
                                ))
                            }
                            <Button
                                rightIcon={<AiOutlineSend/>}
                                colorScheme="teal"
                                variant="solid"
                                onClick={() => {
                                    setLinks([...links, ""]);
                                }}
                                w={"100%"}
                                style={{
                                    marginTop: '10px'
                                }}
                            >
                                Add Product Link
                            </Button>
                        </Flex>
                    </Flex>
                </Flex>
                <Flex justify={"flex-end"} sx={submitContainer}>
                    <Button
                        rightIcon={<AiOutlineSend/>}
                        colorScheme="teal"
                        variant="solid"
                        onClick={handleSubmit}
                        w={"100%"}
                    >
                        Submit
                    </Button>
                </Flex>
            </Card>
            <Card
                padding="30px"
                height="99%"
                width="45%"
                gap={10}
                style={{
                    display: "flex",
                    justifyContent: "center",
                }}
            >
                <Flex direction="column" alignItems="center">
                    {!isLoading ? (
                        <Image
                            src={image}
                            alt='Dan Abramov'/>
                    ) : (
                        <Spinner/>
                    )}
                </Flex>
                <Flex display={'flex'} justifyContent={'center'} w={'full'}>
                    <Button onClick={handleGetProducts}>
                        Get Products
                    </Button>
                </Flex>
            </Card>
            <Slider loading={productsLoading} products={products}/>
        </Flex>
    );
};

export default Recommender;